mapper.select:
SELECT

FROM csai_robot_advanced_settings
WHERE corp_id = #{corpId, jdbcType=BIGINT}

mapper.update:
UPDATE csai_robot_advanced_settings
SET updator = #{updator,jdbcType=VARCHAR},
    update_time = #{updateTime,jdbcType=TIMESTAMP},
    advanced_settings = #{advancedSettings,jdbcType=VARCHAR,typeHandler=handler.JsonTypeHandler}
WHERE corp_id = #{corpId, jdbcType=BIGINT}

mapper.select:
SELECT TRX_ID
FROM INFORMATION_SCHEMA.INNODB_TRX
WHERE TRX_MYSQL_THREAD_ID = CONNECTION_ID();
